import React, {Component} from 'react';
import {Link} from 'react-router-dom';

export default class PostThread extends Component{

    constructor(props){
        super(props);

        //register functions with this
        this.submitThread = this.submitThread.bind(this);
        this.changeTitle = this.changeTitle.bind(this);
        this.changeName = this.changeName.bind(this);
        this.changeBodyText = this.changeBodyText.bind(this);
        this.changeImage = this.changeImage.bind(this);



        this.state={
            error_text: "",
            body_text: "",
            title: "",
            name: "",
            thread_image: "",
        };
    }

    componentDidMount(){

    }

    changeBodyText(e){
        this.setState({
            body_text: e.target.value
        });
    }

    changeTitle(e){
        this.setState({
            title: e.target.value
        });
    }
    
    changeImage(e){
        this.setState({
            thread_image: e.target.value
        });
    }
    
    changeName(e){
        this.setState({
            name: e.target.value
        });
    }
    submitThread(e){
        e.preventDefault();
        if(
            e.body_text.length < 1
            || e.thread_image.length < 1
        ){
            this.setState({
                error_text: "New Thread Fields typed in wrong, requires an image and body."
            });
            return;
        }//if the fields look good then
        this.setState({ // wh
            body_text: e.body_text,
            title: e.title,
            name: e.name,
            thread_image: e.thread_image
        });
        const newThread = {
            body_text: this.state.body_text,
            title: this.state.title,
            name: this.state.name,
            thread_image: this.state.thread_image
        }
        console.log(newThread);

        window.location = "/";
    }

    render(){
        return(
            <div>
                <h3>Post New Thread</h3>
                <form onSubmit={this.submitThread}>
                    <div className="form-group">
                        <label>Title</label>
                        <input type="text"
                        required className="form-control"
                        value={this.state.title}
                        onChange={this.changeTitle}
                        />
                    </div>
                    <div className="form-group">
                        <label>Name</label>
                        <input type="text"
                        required className="form-control"
                        value={this.state.name}
                        onChange={this.changeName}
                        />
                    </div>
                    <div className="form-group">
                        <label>Image</label>
                        <input type="text"
                        required className="form-control"
                        value={this.state.thread_image}
                        onChange={this.changeImage}
                        />
                    </div>
                    <div className="form-group">
                        <label>Body</label>
                        <input type="text"
                        required className="form-control"
                        value={this.state.body_text}
                        onChange={this.changeBodyText}
                        />
                    </div>
                </form>
            </div>
        );
    }
}
