import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import PostThread from './post_thread.component';
import axios from 'axios';

export default class Catalog extends Component{
    constructor(props){
        super(props);

        //register functions
        this.componentDidMount = this.componentDidMount.bind(this);
        this.enterThread = this.enterThread.bind(this);

        this.state = {
            threads: []
        };
    }

    componentDidMount(){
        axios.get("http://localhost:5000/thread")
        .then(res =>{
            console.log(res.data);
            this.setState({threads: res.data});
        });
        console.log("STATE: " + this.state.threads);
    }

    enterThread(e, thread){
        e.preventDefault();
        console.log("Clicked thread: " + thread);
        //window.location = ("http://localhost:5000/thread/" + thread.id);
    }

    render(){
        return(
            <div>
                <PostThread props={this.state.threads} />
                Catalog
                Numthreads: {this.state.threads.length}
                <ul classname="container-fluid">
                    {
                        this.state.threads.map((thread) => (
                            <button onClick={(e) => this.enterThread(e, thread)}>
                                {/* The (e) => solves default button behaviour, and ??? https://reactjs.org/docs/handling-events.html , see Passing Arguments to Even Handlers */}
                                <li key={thread.id} className="row">
                                    <b>{thread.thread_title}</b>
                                    {thread.body_text}
                                </li>
                            </button>
                        ))
                    }
                </ul>
                Le end
            </div>
        );
    }
}
